import React from 'react';

// Props:São atributos que podem ser passados
// Criando um componente do tipo STATIC COMPONENT
const Bemvindo = (props)=>{
    return(
        <div>
            {/* dentro do componente recebendo as propriedades */}
            <h2>Bem Vindo(a) {props.nome}</h2>
            <h3>Tenho {props.anos}</h3>
        </div>
    );
}

function App(){
    return(
        <div>
        {/*Bemvindo é um componente com atributos  */}
        <Bemvindo nome= "Lucas" anos ="31"/>
        <Bemvindo nome= "Eren" anos ="19"/>
        </div>
    );
}

export default App;
